import axios from 'axios'
import * as TYPES from "../redux/actions/types"

export function fetchCurrentUser(authToken){
  return function(dispatch){
    return axios.get('/me', {
      headers: {Authorization: `Bearer ${authToken}`}
    }).then(({ data }) => {
      dispatch({ type: TYPES.CURRENT_USER, payload: data.user })
    })
  }
}

export function fetchAllItems(authToken){
  return function(dispatch){
    dispatch({ type: TYPES.IS_LOADING })
    return axios.get('/items', {
      headers: {Authorization: `Bearer ${authToken}`}
    }).then(({ data }) => {
      dispatch({ type: TYPES.FETCH_ALL_ITEMS, payload: data.items })
      // dispatch({ type: TYPES.IS_LOADED })
    })
  }
}
