  isOpen: false,
  isShow: false,
  isLoading: true,
  isLoggedIn: false,
  isVerifiedAt: '',
  verifyToken: '',
  currentUser: {},
  authToken: '',
  register: {
    email: '',
    password: '',
    fname: '',
    lname: '',
  },
  item: {},
  items: []
}


export const reducer = createReducer(initialState, {
  OPEN_MODAL: (state, action) => {
    // state.first.second[action.someId].fourth = action.someValue
    state.isOpen = true
  }
})

