import {INCREMENT, DECREMENT, RESET} from '../actions/types'

const state = {
  isLoggedIn: false,
  authToken: '',
  items: []
}

export function appReducer(state = state, action) {
  switch (action.type) {
    case INCREMENT:
        console.log('ACTION', action)
      return state + 1
    case DECREMENT:
      return state - 1
    case RESET:
      return 0
    default:
      return state
  }
}
