import * as TYPES from '../actions/types'

const initialState = {
  counter: 0,
  isOpen: false,
  isLoggedIn: false,
  authToken: '',
  items: [
    { name: 'Item 1', description: 'Amet minim mollit non deserunt ullamco est sit aliqua dolor do amet sint. Velit officia consequat duis enim velit mollit. Exercitation veniam consequat sunt nostrud amet.'}
  ]
}

export function reducer(state = initialState, action) {
  switch (action.type) {
    case TYPES.MODAL_TOGGLE:
      return {...state, isOpen: !state.isOpen}
      case TYPES.INCREMENT:
        console.log('ACTION', action)
        console.log('state', state)
      return {...state, counter: state.counter + 1}
    case TYPES.DECREMENT:
      return state - 1
    case TYPES.RESET:
      return 0
    default:
      return state
  }
}
