import {INCREMENT, DECREMENT, RESET} from '../actions/types'

const initialState = {
  counter: 0
}

export function counter(state = initialState, action) {
  switch (action.type) {
    case INCREMENT:
        console.log('ACTION', action)
      return state = {...state, counter: counter + 1}
    case DECREMENT:
      return state - 1
    case RESET:
      return 0
    default:
      return state
  }
}
