export function fetchCurrentUser(authToken) {
  return function (dispatch) {
    // dispatch({ type: 'CURRENT_USER' });
    return axios.get('/me', {
        headers: {Authorization: `Bearer ${authToken}`}
      }).then(response => response.data,
        error => console.log('An error occurred.', error),
    ).then((json) => {
      dispatch({ type: 'CURRENT_USER' });
    },
   );
  };
 }
