import Vue from 'vue'
import { createReducer } from '@reduxjs/toolkit'
import * as Actions from '../actions'

const initialState = {
  isOpen: false,
  isShow: false,
  isLoading: true,
  isLoggedIn: false,
  isVerifiedAt: '',
  verifyToken: '',
  currentUser: {},
  authToken: '',
  register: {
    email: '',
    password: '',
    fname: '',
    lname: '',
  },
  item: {},
  items: []
}

const counterReducer = createReducer(initialState, (builder) => {
  builder
    .addCase(Actions.logout, (state, action) => {
      // state.value++
    })
    .addCase(Actions.openModal, (state, action) => {
      state.isOpen = true
    })
    // .addCase(incrementByAmount, (state, action) => {
    //   state.value += action.payload
    // })
})

export const reducer = createReducer(initialState, {
  OPEN_MODAL: (state, action) => {
    state.isOpen = true
  },
  CLOSE_MODAL: (state, action) => {
    state.isOpen = false
  },
  SHOW_PASSWORD: (state, action) => {
    state.isShow = !state.isShow
  },
  IS_LOADING: (state, action) => {
    state.isLoading = true
  },
  IS_LOADED: (state, action) => {
    state.isLoading = false
  },
  FETCH_ALL_ITEMS: (state, action) => {
    state.items = action.payload
    state.isLoading = false
  },
  REMOVE_ITEM: (state, action) => {
    state.items = state.items.filter((item) => (
      item.uuid !== action.payload
    ))
    state.isLoading = false
  },
  ADD_ITEM: (state, action) => {
    state.item = {}
    state.items = [action.payload, ...state.items],
    state.isLoading = false
    state.isOpen = false
  },
  EDIT_ITEM: (state, action) => {
    state.item = action.payload
    state.isOpen = false
  },
  UPDATE_ITEM: (state, action) => {
    state.item = {}
    const index = state.items.findIndex(i => i.id === action.payload.id)
    state.items[index] = action.payload
    state.isOpen = false
    state.isLoading = false
  },
  REGISTER: (state, action) => {
    Vue.$cookies.set('authToken', action.payload.authToken, '3D')
    state.authToken = action.payload.authToken
    state.currentUser = action.payload.currentUser
    state.isLoggedIn = true
  },
  LOGIN: (state, action) => {
    Vue.$cookies.set('authToken', action.payload.authToken, '3D')
    state.authToken = action.payload.authToken
    state.currentUser = action.payload.currentUser
    state.isLoggedIn = true
  },
  LOGOUT: (state, action) => {
    Vue.$cookies.remove('authToken')
    state.authToken = ''
    state.currentUser = ''
    state.isLoggedIn = false
  },
  CURRENT_USER: (state, action) => {
    state.isLoggedIn = true
    state.currentUser = action.payload
    state.isVerifiedAt = action.payload.email_verified_at
    state.verifyToken = action.payload.email_verification_token
  },
  VERIFY_ACCOUNT: (state, action) => {
    state.currentUser = action.payload
    state.isVerifiedAt = action.payload.email_verified_at
  },
  SET_TOKEN: (state, action) => {
    state.authToken = action.payload
  }
})
