import {ADD_ITEM, REMOVE_ITEM, UPDATE_ITEM} from '../actions/types'

export function itemsReducer(state = '', action) {
  switch (action.type) {
    case ADD_ITEM:
      return 'SOME STATE'
    case REMOVE_ITEM:
      return [...state, 'Remove Item']
    case UPDATE_ITEM:
      return [...state, 'Update Item']
    default:
      return state
  }
}
