import { createSlice } from "@reduxjs/toolkit";

const counter = createSlice({
  name: "counter",
  initialState: {
    count: 0
  },
  reducers: {
    increment(state) {
      state = state + 1;
    },
    decrement(state) {
      state = state - 1;
    },
    reset(state) {
      state = 0;
    },
  },
});

