import Vue from 'vue'
import * as TYPES from '../actions/types'

// const getToken = Vue.$cookies.get('authToken') || ''
// console.log('STY', Vue.$cookies.get('authToken'))

const initialState = {
  isOpen: false,
  isShow: false,
  isLoading: true,
  isLoggedIn: false,
  isVerifiedAt: '',
  verifyToken: '',
  currentUser: {},
  authToken: '',
  register: {
    email: '',
    password: '',
    fname: '',
    lname: '',
  },
  item: {},
  items: []
}

export function reducer(state = initialState, action) {
  switch (action.type) {
    case TYPES.OPEN_MODAL:
      return {...state, isOpen: true}

    case TYPES.CLOSE_MODAL:
      state.item = {}
      return {...state, isOpen: false}

    case TYPES.SHOW_PASSWORD:
      return {...state, isShow: !state.isShow}

    case TYPES.IS_LOADING:
      return {...state, isLoading: true}

    case TYPES.IS_LOADED:
      return {...state, isLoading: false}

    case TYPES.FETCH_ALL_ITEMS: {
      return {
        ...state,
        items: action.payload,
        isLoading: false
      }
    }

    case TYPES.REMOVE_ITEM: {
      const filtered = state.items.filter((item) => (
        item.uuid !== action.payload
      ))
      return {
        ...state,
        items: filtered,
        isLoading: false
      }
    }

    case TYPES.ADD_ITEM: {
      state.item = {}
      return {
        ...state,
        items: [action.payload, ...state.items],
        isOpen: false,
        isLoading: false
      }
    }

    case TYPES.EDIT_ITEM: {
      // state.item = {}
      return {
        ...state,
        item: action.payload,
        isOpen: true
      }
    }

    case TYPES.UPDATE_ITEM: {
      state.item = {}
      const index = state.items.findIndex(i => i.id === action.payload.id)
      state.items[index] = action.payload
      return {
        ...state,
        isOpen: false,
        isLoading: false
      }
    }

    case TYPES.REGISTER: {
      // localStorage.setItem('authToken', authToken)
      Vue.$cookies.set('authToken', authToken, '3D')
      const {authToken, currentUser}  = action.payload
      return {...state,  authToken, currentUser, isLoggedIn: true}
    }

    case TYPES.LOGIN: {
      const {authToken, currentUser}  = action.payload
      console.log('LOGIN_COOKOE', Vue.$cookies)
      Vue.$cookies.set('authToken', authToken, '3D')
      // localStorage.setItem('authToken', authToken)
      return {...state, authToken, currentUser, isLoggedIn: true}
    }
    case TYPES.SET_TOKEN: {
      Vue.$cookies.set('authToken', action.payload, '3D')
      return {
        ...state,
        authToken: action.payload,
      }
    }

    case TYPES.LOGOUT: {
      localStorage.removeItem('authToken')
      return {...state,
        authToken: '',
        currentUser: '',
        isLoggedIn: false
      }
    }

    case TYPES.CURRENT_USER: {
      return {
        ...state,
        isLoggedIn: true,
        currentUser: action.payload,
        isVerifiedAt: action.payload.email_verified_at,
        verifyToken: action.payload.email_verification_token
      }
    }

    case TYPES.VERIFY_ACCOUNT: {
      return {
        ...state,
        currentUser: action.payload,
        isVerifiedAt: action.payload.email_verified_at
      }
    }

    default:
      return state
  }
}
