import axios from 'axios'

function fetchCurrentUser(authToken){
  return function(dispatch){
    return axios.get('/me', {
      headers: {Authorization: `Bearer ${authToken}`}
    }).then(({ data }) => {
      dispatch({type: 'CURRENT_USER', payload: data.user })
    })
  }
}

export default {
  fetchCurrentUser
}
