export async function fetchCurrentUser(authToken){
  if(authToken){
    await axios.get('/me', {
      headers: {Authorization: `Bearer ${authToken}`}
    }).then(({ data }) => ({type: 'CURRENT_USER', payload: data.user }))
  }
}
